INSERT INTO `courses` (`id`, `course`, `content`, `desc`) VALUES
(1, 'PHP', '<h2 class=\"mainHeading\">Course Content and Syllabus for PHP MySQL Training </h2>\r\n            <h3 class=\"text-blue\">Introduction to PHP MySQL</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n               <li>Discussion of Three Tier Architecture</li>\r\n<li>How PHP works with the Web Server</li>\r\n<li>Hardware and Software requirements</li>\r\n<li>Saving data for later</li>\r\n<li>Receiving user input</li>\r\n<li>Repeating code</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Learning PHP</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>PHP Basics</li>\r\n<li>Conditions and Branches</li>\r\n<li>Loops</li>\r\n<li>Variables and Arrays</li>\r\n<li>Strings</li>\r\n<li>Functions</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">MySQL databases</h3>\r\n            <div class=\"col-md-6\">\r\n              <ul class=\"listTick\">\r\n                <li>Database Basics</li>\r\n<li>Loading a Database</li>\r\n<li>MySQL command Interpreter</li>\r\n<li>Creating a database</li>\r\n<li>Modifying a database</li>\r\n<li>Administering a database</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Developing an application using PHP and MySQL</h3>\r\n            <div class=\"col-md-6\">\r\n              <ul class=\"listTick\">\r\n                <li>MySQL functions in PHP</li>\r\n<li>Querying a database</li>\r\n<li>User-Driven Querying</li>\r\n<li>Previous and Next Browsing</li>\r\n<li>Inserts, Updates, and Deletes</li>\r\n<li>User Authentication and Management</li>\r\n<li>Ordering and Shipping</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Three tier architecture for course module </h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n                <li>Client tier</li>\r\n                <li>Middle tier</li>\r\n                <li>Database  tier</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">PHP MySQL Project Work</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n                <li>Development using php/mySQL</li>\r\n<li>Unit Testing</li>\r\n<li>Deployment</li>\r\n<li>Coding Documentation</li>\r\n              </ul>\r\n            </div>', 'PHP Language tack with basic of mysql'),
(2, 'JAVA', '<h3 class=\"text-blue\">Java Language Environment</h3>\r\n<div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n              <li>Object Oriented</li>\r\n<li>Platform Independent</li>\r\n<li>Automatic Memory Management</li>\r\n<li>Compiled / Interpreted approach</li>\r\n<li>Robust</li>\r\n<li>Secure</li>\r\n<li>Dynamic Linking</li>\r\n<li>MultiThreaded</li>\r\n<li>Built-in Networking</li>\r\n              </ul>\r\n            </div>\r\n<h3 class=\"text-blue\">Java Fundamentals</h3>\r\n<div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Data types</li>\r\n<li>Operators</li>\r\n<li>Control Statements</li>\r\n<li>Arrays</li>\r\n<li>Enhanced for-loop</li>\r\n<li>Enumerated types</li>\r\n<li>Static import</li>\r\n<li>Auto boxing</li>\r\n<li>C-style formatted I/O</li>\r\n<li>Variable arguments</li>\r\n              </ul>\r\n            </div>\r\n<h3 class=\"text-blue\">Essentials of Object-Oriented Programming</h3><div class=\"col-md-6\">\r\n              <ul class=\"listTick\">\r\n              <li>Object and Class Definition</li>\r\n<li>Using encapsulation to combine methods and data in a single class</li>\r\n<li>Inheritance and Polymorphism</li>\r\n              </ul>\r\n            </div><h3 class=\"text-blue\">Writing Java Classes</h3>\r\n<div class=\"col-md-6\">\r\n              <ul class=\"listTick\">\r\n  <li>Encapsulation</li>\r\n<li>Polymorphism</li>\r\n<li>Inheritance</li>\r\n<li>OOP in Java</li>\r\n<li>Class Fundamentals</li>\r\n<li>Using Objects</li>\r\n<li>Constructor</li>\r\n<li>Garbage Collection</li>\r\n<li>Method Overloading</li>\r\n<li>Method Overriding</li>\r\n<li>Static Members</li>\r\n<li>Understanding Interface</li>\r\n<li>Using Interfaces class</li>\r\n              </ul>\r\n            </div>\r\n<h3 class=\"text-blue\">Packages</h3>\r\n<div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n           <li>Why packages</li>\r\n<li>Understanding Classpath</li>\r\n<li>Access modifiers and their Scope</li>\r\n              </ul>\r\n            </div><h3 class=\"text-blue\">Exception Handling</h3><div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n               <li>Importance of Exception Handling</li>\r\n<li>Exception Propagation</li>\r\n<li>Exception Types</li>\r\n<li>Using try and catch</li>\r\n<li>throw, throws, finally</li>\r\n<li>Writing User defined Exceptions</li>\r\n              </ul>\r\n            </div><h3 class=\"text-blue\">I/O Operations in Java</h3><div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n              <li>Byte Oriented Streams</li>\r\n<li>File Handling</li>\r\n<li>Readers and Writers</li>\r\n              </ul>\r\n            </div><h3 class=\"text-blue\">Multithreaded Programming</h3><div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>Introduction to Multi-Threading</li>\r\n<li>Understanding Threads and its States</li>\r\n<li>Java Threading Model</li>\r\n<li>Thread class and Runnable Interface</li>\r\n<li>Thread Priorities</li>\r\n<li>Thread Synchronization</li>\r\n<li>Inter thread Communication</li>\r\n<li>Preventing Deadlocks</li>\r\n              </ul>\r\n            </div><h3 class=\"text-blue\">DEVELOPING Java APPS</h3><div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Defining a Solution without Writing Code</li>\r\n<li>Organizing a Concept Solution</li>\r\n<li>Creating a Program Skeleton</li>\r\n<li>Defining Error Checking Requirements</li>\r\n<li>Introduction to Application Security</li>\r\n              </ul>\r\n            </div><h3 class=\"text-blue\">Network Programming</h3><div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Introduction to Networking</li>\r\n<li>InetAddress</li>\r\n<li>URL</li>\r\n<li>TCP Socket and ServerSocket</li>\r\n<li>UDP Socket</li>\r\n<li>Developing a Chat Application</li>\r\n              </ul>\r\n            </div><h3 class=\"text-blue\">Java Util Package / Collections Framework</h3><div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Collection and Iterator Interface</li>\r\n<li>Enumeration</li>\r\n<li>List and ArrayList</li>\r\n<li>Vector</li>\r\n<li>Comparator</li>\r\n<li>Set Interface and SortedSet</li>\r\n<li>Hashtable</li>\r\n<li>Properties</li>\r\n              </ul>\r\n            </div><h3 class=\"text-blue\">Generics</h3><div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Introduction to Generics</li>\r\n<li>Using Built-in Generics Collections</li>\r\n<li>Writing Simple Generic Class</li>\r\n<li>Bounded Generics</li>\r\n<li>Wild Card Generics</li>\r\n              </ul>\r\n            </div><h3 class=\"text-blue\">Inner Classes</h3><div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Nested Top Level Classes</li>\r\n<li>Member Classes</li>\r\n<li>Local Classes</li>\r\n<li>Anonymous Classes</li>\r\n              </ul>\r\n            </div><h3 class=\"text-blue\">Abstract Window Toolkit</h3><div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Graphics</li>\r\n<li>Color and Font</li>\r\n<li>AWT Components/Controls</li>\r\n<li>Event Handling and Layouts</li>\r\n              </ul>\r\n            </div><h3 class=\"text-blue\">Swing Programming</h3><div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Introduction to Swing and MVC Architecture</li>\r\n<li>Light Weight Component</li>\r\n<li>Swing Hierarchy</li>\r\n<li>Atomic Components e.g. JButton, JList and more</li>\r\n<li>Intermediate Container e.g. JPanel, JSplitPane and more</li>\r\n<li>Top-Level Container e.g. JFrame and JApplet</li>\r\n<li>Swing Related Events</li>\r\n              </ul>\r\n            </div>', 'Java Programming'),
(3, 'Dot NET', '<h2 class=\"mainHeading\">Course Content and Syllabus for .NET training in noida</h2>\r\n            <h3 class=\"text-blue\">.NET Course Contents</h3>\r\n			 <h3 class=\"text-blue\">Introduction to .NET</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n              <li>.Net Framework and CLR</li>\r\n<li>Introduction to Visual studio</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Introduction to C#</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n                 <li>Overview of C#</li>\r\n                 <li>Data Types</li>\r\n                 <li>Memory Concepts</li>\r\n                 <li>Control Structures</li>\r\n                 <li>Arrays</li>\r\n				 <li>Namespaces</li>\r\n              </ul>\r\n            </div>\r\n			\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Object Oriented Programming</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			     <li>Classes and Objects</li>\r\n				 <li>Constructors</li>\r\n				 <li>Inheritance</li>\r\n				 <li>Polymorphism</li>\r\n				 <li>Abstract</li>\r\n				 <li>Interface</li>\r\n				 <li>Delegates</li>\r\n				 <li>Exception Handling</li>\r\n				 <li>Reflections</li>\r\n				 <li>Indexers</li>\r\n				 <li>Threading</li>\r\n			  </ul>\r\n             </div>	 \r\n			 \r\n			 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Graphical User Interface</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			     <li>Intro to Win forms</li>\r\n				 <li>Event handling</li>\r\n				 <li>Toolbox</li>\r\n				 <li>Custom toolbox controls</li>\r\n				 <li>User defined controls</li>\r\n				 <li>Graphic controls</li>\r\n				 <li>Files and streams</li>\r\n				 <li>Usage and implementations of controls</li>\r\n				 <li>Software Development</li>\r\n			  </ul>\r\n             </div>	  \r\n			 \r\n			  <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">MSSQL Server</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			     <li>Intro to databases</li>\r\n				 <li>Relational database concepts</li>\r\n				 <li>Structured Query Language</li>\r\n				 <li>Stored Procedures</li>\r\n				 <li>Functions</li>\r\n				 <li>Triggers</li>\r\n			  </ul>\r\n             </div>	 \r\n			 \r\n			  <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">ADO.NET</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			     <li>Basics of Ado.net</li>\r\n				 <li>Connected and Disconnected modes</li>\r\n				 <li>Programming with Ado.net</li>\r\n				 <li>CSS and JavaScript</li>\r\n				 <li>HTML</li>  \r\n			  </ul>\r\n             </div>	  \r\n			 \r\n			 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">ASP.NET</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			     <li>Intro to Web forms</li>\r\n				 <li>Web Controls</li>\r\n				 <li>Server Controls</li>\r\n				 <li>Client Controls</li>\r\n				 <li>Navigation Controls</li>\r\n				 <li>Validations</li>\r\n				 <li>Master Page</li>\r\n				 <li>State Management Techniques</li>\r\n				 <li>Caching</li>\r\n				 <li>AJAX</li>\r\n				 <li>Web Services</li>\r\n				 <li>WPF</li>\r\n				 <li>Silverlight</li> \r\n			  </ul>\r\n             </div>	 \r\n			 \r\n			  <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Project Work</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			     <li>Development</li>\r\n				 <li>Unit Testing</li>\r\n				 <li>Deployment</li>\r\n				 <li>Documentation</li>\r\n			   </ul></div>', 'Dot Net MVC'),
(4, 'Python', '<h3 class=\"text-blue\">Python Course Content</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">An Introduction to Python</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n                  <li>Introductory Remarks about Python</li>\r\n                  <li>A Brief History of Python</li>\r\n                  <li>How python is differ from other languages</li>\r\n                  <li>Python Versions</li>\r\n                  <li>Installing Python</li>\r\n                  <li>IDLE</li>\r\n                  <li>Getting Help</li>\r\n                  <li>How to execute Python program</li>\r\n                  <li>Writing your first program </li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Python Basics</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n                   <li>Python keywords and Identifiers</li>\r\n                   <li>Python statements</li>\r\n                   <li>Python indentation</li>\r\n                   <li>Comments in python</li>\r\n                   <li>command line arguments</li>\r\n                   <li>Getting user input</li>\r\n                   <li>Exercise</li>\r\n               </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Variables and data types<h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n                   <li>Introduction</li>\r\n				   <li>Variables</li>\r\n				   <li>Data types</li>\r\n				   <li>Numbers</li>\r\n				   <li>Strings</li>\r\n				   <li>Lists, tuples &amp; Dictionary</li>\r\n				   <li>Exercise</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Decision making &amp; Loops</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n                   <li>Introduction</li>\r\n				   <li>Control Flow and Syntax</li>\r\n				   <li>The if Statement</li>\r\n				   <li>Python Operators</li>\r\n				   <li>The while Loop</li>\r\n				   <li>break and continue</li>\r\n				   <li>The for Loop</li>\r\n				   <li>Pass statement</li>\r\n				   <li>Exercise</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Functions</h3>\r\n            <div class=\"col-md-12\">\r\n		       <ul class=\"listTick\">\r\n				   <li>Introduction</li>\r\n				   <li>Calling a function</li>\r\n				   <li>Function arguments</li>\r\n				   <li>Built in functions</li>\r\n				   <li>Scope of variables</li>\r\n				   <li>Decorators</li>\r\n				   <li>Passing Functions to a Function</li>\r\n				   <li>Lambda</li>\r\n				   <li>Closures</li>\r\n				   <li>Exercise</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Modules and Packages</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			       <li>Modules</li>\r\n				   <li>Importing module</li>\r\n				   <li>Standard Module - sys</li>\r\n				   <li>Standard Module - OS</li>\r\n				   <li>The dir Function</li>\r\n				   <li>Packages</li>\r\n				   <li>Exercise</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Exception Handling</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			       <li>Errors</li>\r\n				   <li>Run Time Errors</li>\r\n				   <li>Handling IO Exceptions</li>\r\n				   <li>Try.... except statement</li>\r\n				   <li>Raise</li>\r\n				   <li>Assert</li>\r\n				   <li>Exercise</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Files and Directories</h3>\r\n            <div class=\"col-md-12\">\r\n			<ul class=\"listTick\">\r\n                   <li>Introduction</li>\r\n				   <li>Writing Data to a File</li>\r\n				   <li>Reading Data From a File</li>\r\n				   <li>Additional File Methods</li>\r\n				   <li>Working with files</li>\r\n				   <li>Working with Directories</li>\r\n				   <li>The pickle Module</li>\r\n				   <li>Exercise</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Classes Objects</h3>\r\n            <div class=\"col-md-12\">\r\n		      <ul class=\"listTick\">\r\n                   <li>Introduction classes and objects</li>\r\n				   <li>Creating Classes</li>\r\n				   <li>Instance Methods</li>\r\n				   <li>Special class method</li>\r\n				   <li>Inheritance</li>\r\n				   <li>Method overriding</li>\r\n				   <li>Data hiding</li>\r\n				   <li>Exercise</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Regular Expressions</h3>\r\n            <div class=\"col-md-12\">\r\n		      <ul class=\"listTick\">\r\n                   <li>Introduction</li>\r\n				   <li>Match function</li>\r\n				   <li>Search function</li>\r\n				   <li>Grouping</li>\r\n				   <li>Matching at Beginning or End</li>\r\n				   <li>Match Objects</li>\r\n				   <li>Flags</li>\r\n				   <li>Exercise</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Socket programming</h3>\r\n            <div class=\"col-md-12\">\r\n			  <ul class=\"listTick\">\r\n                   <li>What are sockets?</li>\r\n				   <li>Creating sockets</li>\r\n				   <li>Server-client socket methods</li>\r\n				   <li>Connecting client server</li>\r\n				   <li>Client-server chatting program</li>\r\n				   <li>Exercise</li>\r\n              </ul>\r\n            </div>\r\n            ', 'Python Programming Languages and Framework'),
(5, 'MySql', '<h3 class=\"text-blue\">MySQL Course Content</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n              <li>A brief history of databases</li>\r\n<li>Relational database concepts</li>\r\n<li>Database tables</li>\r\n<li>MySQL Data types</li>\r\n<li>Introduction to PHPMyadmin</li>\r\n<li>Creating and Checking Tables</li>\r\n<li>Auto_increment and Primary Keys</li>\r\n<li>Inserting Values into Tables</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Mini Project with PHP and MySQL</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Creation of database and required tables using PHPMyadmin.</li>\r\n<li>Creation of required templates, pages and managing site using IDEDreamweaver.</li>\r\n<li>Keeping structure, style and behavior of created pages apart.</li>\r\n<li>Creating login and logout pages for maintaining sessions.</li>\r\n<li>Creating forms to insert data into address book-personal details, image etc.</li>\r\n<li>Creating page to view the data from address book.</li>\r\n<li>Adding PHPScript for form validation.</li>\r\n<li>Adding server side validation.</li>\r\n<li>Adding mouse events like mouseout and mouseover dynamically.</li>\r\n<li>Creating page to edit or delete the records in the address book.</li>\r\n<li>Creating page to enable searching the address book.</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Introduction to Ajax</h3>\r\n            <div class=\"col-md-6\">\r\n              <ul class=\"listTick\">\r\n              <li>What is ajax</li>\r\n<li>Connecting to server using PHPScript</li>\r\n<li>Downloading data behind scenes</li>\r\n<li>Modifying web pages on the fly</li>\r\n<li>Creating an XMLHttpRequest Object in different browsers</li>\r\n<li>Checking the XMLHttpRequest Object\'s readyState property</li>\r\n<li>Checking the XMLHttpRequest Object\'s status property</li>\r\n<li>Getting Your Data With the XMLHttpRequest Object</li>\r\n<li>Problems creating an XMLHttpRequest Object</li>\r\n<li>Fetching Text Data From the server</li>\r\n<li>Using the text data received through XMLHttpRequest</li>\r\n             </ul>\r\n            </div>', 'Mysql Database'),
(6, 'Linux', '<h3 class=\"text-blue\">Linux Course Content</h3>\r\n		   <div class=\"col-md-12\">\r\n		   </div>\r\n		   <div class=\"clearfix\"></div>\r\n		   <h3 class=\"text-blue\">Linux basic concepts &amp; installations</h3>\r\n			<div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>Installation in Standalone Machine</li>\r\n<li>Installation through NFS</li>\r\n<li>Installation through FTP</li>\r\n<li>Installation through HTTP</li>\r\n<li>Installation through KICKSTART</li>	\r\n              </ul>\r\n            </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">File System Management :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Creation of File Systems</li>\r\n<li>Understanding EXT2, EXT3&amp; EXT4 File Systems</li>\r\n<li>Converting Ext2 to Ext3 file systems</li>\r\n<li>Reverting back from Ext3 to Ext2 files systems</li>\r\n<li>Understanding fdisk,e2label, mount, umount commands</li>\r\n<li>Understanding fstab and mtab files</li>\r\n<li>TROUBLESHOOTING</li>\r\n              </ul>\r\n              </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">User management :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding different types of groups and creation of groups</li>\r\n<li>Creation of users in different groups</li>\r\n<li>Understanding Passwd, Shadow Files</li>\r\n<li>Understanding passwd aging</li>\r\n<li>Creation of quotas for users, groups and file systems</li>\r\n<li>Understanding users security files</li>\r\n<li>The different commands for Monitoring the users</li>\r\n<li>TROUBLESHOOTING</li>\r\n              </ul>\r\n              </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Run levels :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding the different types of run-levels</li>\r\n<li>Understanding different types of shutdown commands</li>\r\n<li>Understanding run control scripts</li>\r\n<li>Understanding the different types</li>\r\n              </ul>\r\n              </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Network file system (NFS) :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding NFS server and NFS clients</li>\r\n<li>Understanding daemons and files in NFS of boot phases</li>\r\n<li>Configuring NFS server and different NFS clients</li>\r\n<li>Configuration of autofs, NFS security</li>\r\n              </ul>\r\n              </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">File transfer protocol :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding the features and advantages of FTP server</li>\r\n<li>Configuring FTP server and FTP clients</li>\r\n<li>Configuring FTP server for anonymous and real users with download and upload permissions</li>\r\n<li>Configuring FTP user access, FTP security</li>\r\n<li>Understanding FTP Basic Commands</li>\r\n<li>Configuring of anonymous FTP Server</li>\r\n              </ul>\r\n              </div>\r\n			  <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">XINETD :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding xinetd based and non xinetd based services</li>\r\n<li>Configuring xinetd based services</li>\r\n<li>Xinetd security</li>\r\n              </ul>\r\n              </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">TCP WRAPPER :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding TCP WRAPPER</li>\r\n<li>Configuring TCP WRAPPER for services</li>           \r\n            </ul>\r\n             </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Domain naming service :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding DNS service and different types of DNS servers</li>\r\n<li>Configuring DNS (master) DNS (Slave)</li>\r\n<li>Understanding &amp; Configuring forward (DNS) and cache (DNS) of boot phases</li>\r\n<li>Understanding different types of files when the system is booting</li>\r\n<li>TROUBLESHOOTING</li>           \r\n            </ul>\r\n             </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Logical volume manager (LVM) :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Creation of file systems and converting into LVM</li>\r\n<li>Creation of physical partitions</li>\r\n<li>Creation of volume groups</li>\r\n<li>Creation of logical partitions</li>\r\n<li>Extending the volume group</li>\r\n<li>Extending the logical partitions</li>           \r\n            </ul>\r\n             </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Package Manager (RPM) :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding the features and advantages of RPM</li>\r\n<li>Installation of RPM packages</li>\r\n<li>Upgradation of RPM</li>\r\n<li>Verification of RPM</li>\r\n<li>Q u e r y i n g , T R O U B L E SHOOTING</li>          \r\n            </ul>\r\n             </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Backup and recovery :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding different types of file system backup</li>\r\n<li>Understanding different types of files backups</li>\r\n<li>Understanding different types of file system backups</li>\r\n<li>Understanding different types of dump levels</li>\r\n<li>Understanding monthly, weekly, daily backups</li>\r\n<li>Different types of backup strategies</li>           \r\n            </ul>\r\n             </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Automation of jobs :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>At Jobs . Cron Jobs</li>          \r\n            </ul>\r\n             </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Network information service:</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding NIS and daemons at NIS (Server, Slave and Clients)</li>\r\n<li>Configuring NIS (master), NIS (slave) and NIS clients in heterogeneous environments</li>\r\n<li>Integrating NIS (master and slave) with NFS Server</li>           \r\n            </ul>\r\n             </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Dynamic host configuration protocol :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding DHCP</li>\r\n<li>Configuring DHCP server for different DHCP clients</li>           \r\n            </ul>\r\n             </div>\r\n			 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">APACHE :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding APACHE</li>\r\n<li>Configuring APACHE web server with virtual hosting</li>\r\n<li>Configuring APACHE web server with IP BASED, HOST BASED and PORT BASED</li>\r\n<li>Configuring member logins for APACHE web server</li>          \r\n            </ul>\r\n             </div>\r\n			 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">SAMBA :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding the features and advantages of SAMBA server</li>\r\n<li>Configuring SAMBA for heterogeneous environment</li>\r\n<li>Sharing the resources between unix to unix using SAMBA</li>\r\n<li>Sharing the resources between windows to unix (vice-versa)</li>\r\n<li>SAMBA security</li>           \r\n            </ul>\r\n             </div>\r\n			 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">SQUID :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding SQUID proxy</li>\r\n<li>Configuring SQUID proxy with ACL</li>           \r\n            </ul>\r\n             </div>\r\n			 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Raid levels :</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Understanding RAID LEVELS</li>\r\n<li>Configuring different types of RAID LEVELS (0,1and5)</li>           \r\n            </ul>\r\n            </div>', 'Linux System Administration'),
(7, 'Oracle', '<h3 class=\"text-blue\">INTRODUCTION TO ORACLE DBA</h3>\r\n			<div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>What is DBA ?</li>\r\n<li>Why a Company needs a DBA?</li>\r\n<li>Roles &amp; Responsibilities of DBA</li>\r\n<li>Oracle Architecture</li>\r\n<li>Physical and Logical Phase of Database</li>\r\n<li>Types of files(Control file, Redolog file and Datafile)</li>\r\n<li>Different types of Datafiles</li>\r\n<li>Instance</li>\r\n<li>SGA(System Global Area) and Background Process</li>\r\n              </ul>\r\n            </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">DATABASE CREATION</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Different Startup Modes</li>\r\n<li>Different Shutdown Modes</li>\r\n<li>Types of Database Creation</li>\r\n<li>Manual Method of Creation</li>\r\n              </ul>\r\n              </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">TABLESPACE MANAGEMENT</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Introduction to Table space</li>\r\n<li>Types of Table space(BIG &amp; SMALL)</li>\r\n<li>Temporary Table space</li>\r\n<li>Altering/Renaming a Table space</li>\r\n<li>Renaming a data file</li>\r\n<li>Temporary Groups</li>\r\n<li>Table space Encryption(11g)</li>\r\n              </ul>\r\n              </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">STORAGE PARAMETERS</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>What are segments?</li>\r\n<li>What are extents?</li>\r\n<li>What is Blocks?</li>\r\n<li>Oracle Blocks</li>\r\n<li>Estimation of Extents</li>\r\n<li>Dictionary Managed Table space</li>\r\n<li>Locally Managed Table space</li>\r\n<li>Storage Parameters(Initial, Next, Min Extents, Xtents, Pctincrease)</li>\r\n              </ul>\r\n              </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">USER MANAGEMENT</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Who are Database Users?</li>\r\n<li>Why to create a User?</li>\r\n<li>Creation of Users</li>\r\n<li>Assigning Privileges</li>\r\n<li>Creating and granting Roles</li>\r\n<li>Allocating table space Quotas to Users</li>\r\n<li>Assigning default table space for the Users</li>\r\n<li>Setting Profiles for the Users</li>\r\n<li>Levels of Authentication</li>\r\n<li>Schema Management(11g)</li>\r\n<li>Security(11g)</li>\r\n              </ul>\r\n              </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">UNDO MANAGEMENT</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>What is Undo?</li>\r\n<li>Why Undo required?</li>\r\n<li>Pending offline status</li>\r\n<li>Brief idea about Rollback segments</li>\r\n<li>Flashback Features</li>\r\n<li>Flashback Table</li>\r\n<li>Flashback query</li>\r\n<li>Flashback Version query</li>\r\n              </ul>\r\n              </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">What is spfile?</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Difference between spfile and pfile</li>\r\n<li>Advantages of spfile</li>\r\n<li>What is OMF?</li>\r\n<li>Advantages of OMF</li>\r\n<li>Files conventions of OMF</li>\r\n              </ul>\r\n              </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">NETWORKING IN ORACLE</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Introduction to Oracle Networking</li>\r\n<li>Network default location</li>\r\n<li>Editing Network file</li>\r\n              </ul>\r\n              </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Tnsnames.ora</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Introduction to Database Links</li>\r\n<li>Types of Database Links</li>\r\n<li>Introduction to Materialized views</li>\r\n<li>Advantages of Materialized view</li>\r\n<li>Refresh Modes</li>\r\n              </ul>\r\n              </div>\r\n			   <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">REDO-LOG &amp; CONTROLFILE MANAGEMENT</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Introduction to Redo log</li>\r\n<li>Status of Redo logs</li>\r\n<li>Multiplexing the Redo log files</li>\r\n<li>Re-creation of Control file</li>\r\n<li>Renaming the Database</li>\r\n<li>Multiplexing the Control file</li>\r\n              </ul>\r\n              </div>\r\n			  <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">LOGICAL BACKUP</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>What is a logical backup</li>\r\n<li>Why it is required</li>\r\n<li>Export/import Utility</li>\r\n<li>Data Pump</li>\r\n<li>Difference between Data Pump and Traditional Export/import</li>\r\n<li>Impdp/expdp</li>\r\n<li>Transportable Table spaces</li>\r\n              </ul>\r\n              </div>\r\n			  <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">PHYSICAL BACKUP</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Introduction to Physical backup</li>\r\n<li>Types of Physical backup</li>\r\n<li>Cold &amp; Hot backup</li>\r\n<li>Recovery scenarios</li>\r\n<li>RMAN backup</li>\r\n<li>RMAN Point in time recovery</li>\r\n<li>RMAN 11g new features</li>\r\n              </ul>\r\n              </div>\r\n			  <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">PEFORMANCE TUNING</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>What is Performance Tuning?</li>\r\n<li>Why Tuning required?</li>\r\n<li>Types of Tuning</li>\r\n<li>Database Tuning</li>\r\n<li>Tuning</li>\r\n<li>SQL Tuning</li>\r\n<li>Memory Tuning</li>\r\n<li>Network Tuning</li>\r\n<li>Partitioning and Indexes</li>\r\n<li>Partitioning Enhancements</li>\r\n<li>AWR/ASH/ASMM/ADDM</li>\r\n<li>RAT(Real Application Testing)(11g)</li>\r\n<li>SQL Performance Analyses (11g)</li>\r\n<li>Row chaining and Row migration</li>\r\n<li>Resemble Table spaces</li>\r\n              </ul>\r\n            </div>', 'Oracle Database'),
(8, 'SalesForce', '<h3 class=\"text-blue\">Sales Force Course Content:</h3>\r\n            <div class=\"col-md-12\">\r\n			</div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Cloud Computing Fundamentals:</h3>\r\n			<div class=\"col-md-12\">\r\n			<ul class=\"listTick\">\r\n             <li>Introduction to the Cloud Computing</li>\r\n<li>Evolution of Cloud Computing</li>\r\n<li>Comparisons with other computing techniques fetchers</li>\r\n<li>Key characteristics of cloud computing</li>\r\n<li>Advantages/Disadvantages</li>\r\n<li>Classification of Cloud Computing</li>\r\n<li>Based on as a service model</li>\r\n<li>SAAS (Software as a service)</li>\r\n<li>PAAS (Platform as a service)</li>\r\n<li>IAAS (Infrastructure as a service</li>\r\n<li>Based on deployment or access model</li>\r\n<li>Cloud</li>\r\n<li>Private Cloud</li>\r\n<li>Hybrid Cloud</li>\r\n            </ul>\r\n			</div>\r\n			<div class=\"clearfix\"></div>\r\n			  <h3 class=\"text-blue\">Sales Force CRM Concepts:</h3>\r\n			  <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>What is CRM?</li>\r\n<li>How CRM can help?</li>\r\n<li>Sales force CRM Navigation terminology</li>\r\n<li>Compare Salesforce.com CRM with other CRM products</li>\r\n<li>Customization in sales force .com CRM</li>\r\n<li>Introduction to sales force CRM Setup tool</li>\r\n<li>Personal Setup</li>\r\n<li>App Setup</li>\r\n<li>Admin Setup</li>\r\n              </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			  <h3 class=\"text-blue\">Salesforce.Com Cloud Overview:</h3>\r\n			  <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>Sales Cloud</li>\r\n<li>Service Cloud</li>\r\n<li>Collaboration Cloud</li>\r\n<li>Project development life cycle</li>\r\n<li>Sales force editions</li>\r\n<li>Apex data loader</li>\r\n              </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			  <h3 class=\"text-blue\">Organization Administration:</h3>\r\n			  <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>Set your organization&rsquo;s language &amp; locale</li>\r\n<li>Manage currency</li>\r\n              </ul>\r\n			  <div class=\"clearfix\"></div>\r\n			  <h3 class=\"text-blue\">Customization:</h3>\r\n			  <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n           <li>Create custom profiles &amp; custom fields</li>\r\n<li>Define dependent pick list</li>\r\n<li>Customize lookup fields</li>\r\n<li>Customize validation &amp; formulas</li>\r\n<li>Customize page layouts</li>\r\n<li>Customize standard related lists</li>\r\n<li>Use field-level security</li>\r\n              </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			  <h3 class=\"text-blue\">Workflow:</h3>\r\n			  <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>Define workflow</li>\r\n<li>Set up workflow rules</li>\r\n<li>Set up workflow tasks &amp; alerts</li>\r\n              </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			   <h3 class=\"text-blue\">Workflow Approvals:</h3>\r\n			   <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n           <li>Plan approvals using workflow</li>\r\n<li>Use the approval wizard &ndash; standard vs. jump start</li>\r\n<li>Create workflow approvals</li>\r\n              </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			  <h3 class=\"text-blue\">Security &amp; Access:</h3>\r\n			  <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>Create &amp; Manager Users</li>\r\n<li>Set organization-wide defaults</li>\r\n<li>Learn about record accessed</li>\r\n<li>Create the role hierarchy</li>\r\n<li>Learn about role transfer &amp; mass Transfer functionality</li>\r\n<li>Profiles, Login History</li>\r\n              </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			   <h3 class=\"text-blue\">Data Utilities:</h3>\r\n			   <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n           <li>Importing overview</li>\r\n<li>Learn about import solutions &amp; Custom Object records using the data loader</li>\r\n<li>User mass delete</li>\r\n<li>Initiate weekly export</li>\r\n<li>Use storage</li>\r\n              </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			  <h3 class=\"text-blue\">Analytics:</h3>\r\n			  <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>Create custom reports</li>\r\n<li>Use advanced filters</li>\r\n<li>Use conditional highlighting</li>\r\n<li>Use custom summary formulas</li>\r\n<li>Use dashboards</li>\r\n              </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			   <h3 class=\"text-blue\">Extending Salesforce CRM:</h3>\r\n			   <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n          <li>Learn about custom objects</li>\r\n<li>Learn about custom tabs</li>\r\n<li>Learn about custom web tabs</li>\r\n              </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			  <h3 class=\"text-blue\">The Appexchnage:</h3>\r\n			  <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>The AppExchange</li>\r\n<li>Install an app</li>\r\n<li>Delete an app</li>\r\n              </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			   <h3 class=\"text-blue\">Visual Force:</h3>\r\n			   <div class=\"col-md-12\">\r\n			   <ul class=\"listTick\">\r\n          <li>Introduction and Tools</li>\r\n<li>Purpose of Visualfoce</li>\r\n<li>MVC Architecture</li>\r\n              </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			  <h3 class=\"text-blue\">Controllers:</h3>\r\n			  <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>Variables and Formulas</li>\r\n<li>Standard Controllers and Standard List Controllers</li>\r\n<li>Custom Controllers and Controller Extensions</li>\r\n<li>Using static resources and custom components</li>\r\n<li>Styling VF pages</li>\r\n<li>Overriding buttons, links and tabs with VF</li>\r\n<li>Using JavaScript in VF Pages</li>\r\n<li>Advanced examples</li>\r\n              </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			   <h3 class=\"text-blue\">APEX:</h3>\r\n			   <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n           <li>Introduction to Apex</li>\r\n<li>Pupose of Apex</li>\r\n              </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			  <h3 class=\"text-blue\">Apex Fundamentals</h3>\r\n              <ul class=\"listTick\">\r\n             <li>Collections</li>\r\n<li>Loops</li>\r\n              </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			  <h3 class=\"text-blue\">Apex Scheduling:</h3>\r\n			  <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n           <li>Triggers</li>\r\n<li>Invoking Apex</li>\r\n<li>Classes, Objects and Interfaces</li>\r\n<li>Testing Apex</li>\r\n<li>Dynamic Apex</li>\r\n<li>Batch Apex</li>\r\n<li>Debugging Apex</li>\r\n<li>Deploying Apex</li>\r\n<li>Developing Apex in managed packages</li>            \r\n			  </ul>\r\n			  </div>\r\n			  <div class=\"clearfix\"></div>\r\n			  <h3 class=\"text-blue\">sObjects and the Database</h3>\r\n			  <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n           <li>sObjects and the Database</li>\r\n<li>What is an sObject</li>\r\n<li>SOQL and SOSL Queries</li>           \r\n			  </ul>\r\n            </div>', 'Salesforce'),
(9, 'SAP', 'ABAP\r\nTechnical module, basically a language for business applications\r\nBASIS\r\nTechnical module who responsible for configuring and maintaining the operating system and database of SAP\r\nBI/BW\r\nLeading data warehousing and reporting tool\r\nSD\r\nFunctional Module for Sales & Distribution\r\nCO\r\nFunctional Module for cost management\r\nCRM\r\nFunctional module Customer Relationship Management\r\nFI\r\nFunctional Module for company-wide control and integration of financial information Projects\r\nPP\r\nFunctional Module for Production Planning\r\nMM\r\nFunctional Module for Material Management', 'Best SAP Training');
INSERT INTO `courses` (`id`, `course`, `content`, `desc`) VALUES
(10, 'CCNA/CCNP', '<h3 class=\"text-blue\">Describe how a network works</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			  <li>Describe the purpose and functions of various network devices</li>\r\n<li>Describe the purpose and functions of various network devices</li>\r\n<li>Select the components required to meet a network specification</li>\r\n<li>Use the OSI and TCP/IP models and their associated protocols to explain how data flows in a network</li>\r\n<li>Describe common networked applications including web applications</li>\r\n<li>Describe the purpose and basic operation of the protocols in the OSI and TCP models</li>\r\n<li>Describe the impact of applications (Voice Over IP and Video Over IP) on a network</li>\r\n<li>Interpret network diagrams</li>\r\n<li>Determine the path between two hosts across a network</li>\r\n<li>Describe the components required for network and Internet communications</li>\r\n<li>Identify and correct common network problems at layers 1, 2, 3 and 7 using a layered model approach</li>\r\n<li>Differentiate between LAN/WAN operation and features</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Configure, verify and troubleshoot a switch with VLANs and interswitch communications</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Select the appropriate media, cables, ports, and connectors to connect switches to other network devices and hosts</li>\r\n<li>Explain the technology and media access control method for Ethernet networks</li>\r\n<li>Explain network segmentation and basic traffic management concepts</li>\r\n<li>Explain basic switching concepts and the operation of Cisco switches</li>\r\n<li>Perform and verify initial switch configuration tasks including remote access management</li>\r\n<li>Verify network status and switch operation using basic utilities (including: ping, traceroute, telnet, SSH, arp, ipconfig), SHOW &amp; DEBUG commands</li>\r\n<li>Identify, prescribe, and resolve common switched network media issues, configuration issues, auto negotiation, and switch hardware failures</li>\r\n<li>Describe enhanced switching technologies (including: VTP, RSTP, VLAN, PVSTP, 802.1q)</li>\r\n<li>Describe how VLANs create logically separate networks and the need for routing between them</li>\r\n<li>Configure, verify, and troubleshoot VLANs</li>\r\n<li>Configure, verify, and troubleshoot trunking on Cisco switches</li>\r\n<li>Configure, verify, and troubleshoot interVLAN routing</li>\r\n<li>Configure, verify, and troubleshoot VTP</li>\r\n<li>Configure, verify, and troubleshoot RSTP operation</li>\r\n<li>Interpret the output of various show and debug commands to verify the operational status of a Cisco switched network.</li>\r\n<li>Implement basic switch security (including: port security, trunk access, management vlan other than vlan1, etc.)</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Implement an IP addressing scheme and IP Services to meet network requirements</h3>\r\n            <div class=\"col-md-12\">\r\n            <ul class=\"listTick\">\r\n			<li>Describe the operation and benefits of using private and public IP addressing</li>\r\n<li>Explain the operation and benefits of using DHCP and DNS</li>\r\n<li>Configure, verify and troubleshoot DHCP and DNS operation on a router.(including: CLI/SDM)</li>\r\n<li>Implement static and dynamic addressing services for hosts in a LAN environment</li>\r\n<li>Calculate and apply an addressing scheme including VLSM IP addressing design to a network</li>\r\n<li>Determine the appropriate classless addressing scheme using VLSM and summarization to satisfy addressing requirements in a LAN/WAN environment</li>\r\n<li>Describe the technological requirements for running IPv6 in conjunction with IPv4 (including: protocols, dual stack, tunneling, etc).</li>\r\n<li>Describe IPv6 addresses</li>\r\n<li>Identify and correct common problems associated with IP addressing and host configurations</li>\r\n</ul>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n           <h3 class=\"text-blue\">Configure, verify and troubleshoot a switch with VLANs and interswitch communications</h3>\r\n            <div class=\"col-md-12\">\r\n<ul class=\"listTick\">\r\n<li>Describe basic routing concepts (including: packet forwarding, router lookup process)</li>\r\n<li>Describe the operation of Cisco routers (including: router bootup process, POST, router components)</li>\r\n<li>Select the appropriate media, cables, ports, and connectors to connect routers to other network devices and hosts</li>\r\n<li>Configure, verify, and troubleshoot RIPv2</li>\r\n<li>Access and utilize the router to set basic parameters.(including: CLI/SDM)</li>\r\n<li>Connect, configure, and verify operation status of a device interface</li>\r\n<li>Verify device configuration and network connectivity using ping, traceroute, telnet, SSH or other utilities</li>\r\n<li>Perform and verify routing configuration tasks for a static or default route given specific routing requirements</li>\r\n<li>Manage IOS configuration files. (including: save, edit, upgrade, restore)</li>\r\n<li>Manage Cisco IOS</li>\r\n<li>Compare and contrast methods of routing and routing protocols</li>\r\n<li>Configure, verify, and troubleshoot OSPF</li>\r\n<li>Configure, verify, and troubleshoot EIGRP</li>\r\n<li>Verify network connectivity (including: using ping, traceroute, and telnet or SSH)</li>\r\n<li>Troubleshoot routing issues</li>\r\n<li>Verify router hardware and software operation using SHOW &amp; DEBUG commands.</li>\r\n<li>Implement basic router security</li>\r\n</ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Explain and select the appropriate administrative tasks required for a WLAN</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n       <li>Describe standards associated with wireless media (including: IEEE WI-FI Alliance, ITU/FCC)</li>\r\n<li>Identify and describe the purpose of the components in a small wireless network. (Including: SSID, BSS, ESS)</li>\r\n<li>Identify the basic parameters to configure on a wireless network to ensure that devices connect to the correct access point</li>\r\n<li>Compare and contrast wireless security features and capabilities of WPA security (including: open, WEP, WPA-1/2)</li>\r\n<li>Identify common issues with implementing wireless networks. (Including: Interface, miss configuration)</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Identify security threats to a network and describe general methods to mitigate those threats</h3>\r\n            <div class=\"col-md-12\">\r\n		<ul class=\"listTick\">\r\n              <li>Describe today\'s increasing network security threats and explain the need to implement a comprehensive security policy to mitigate the threats</li>\r\n<li>Explain general methods to mitigate common security threats to network devices, hosts, and applications</li>\r\n<li>Describe the functions of common security appliances and applications</li>\r\n<li>Describe security recommended practices including initial steps to secure network devices</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Implement, verify, and troubleshoot NAT and ACLs in a medium-size Enterprise branch office network</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			<li>Describe the purpose and types of ACLs</li>\r\n<li>Configure and apply ACLs based on network filtering requirements.(including: CLI/SDM)</li>\r\n<li>Configure and apply an ACLs to limit telnet and SSH access to the router using (including: SDM/CLI)</li>\r\n<li>Verify and monitor ACLs in a network environment</li>\r\n<li>Troubleshoot ACL issues</li>\r\n<li>Explain the basic operation of NAT</li>\r\n<li>Configure NAT for given network requirements using (including: CLI/SDM)</li>\r\n<li>Troubleshoot NAT issues</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Implement and verify WAN links</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Describe different methods for connecting to a WAN</li>\r\n<li>Configure and verify a basic WAN serial connection</li>\r\n<li>Configure and verify Frame Relay on Cisco routers</li>\r\n<li>Troubleshoot WAN implementation issues</li>\r\n<li>Describe VPN technology (including: importance, benefits, role, impact, components)</li>\r\n<li>Configure and verify a PPP connection between Cisco routers</li>\r\n             </ul>\r\n            </div>\r\n<h3 class=\"text-blue\">CCNP Course Content</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Implementing Cisco IP Routing (300-101)</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Describing network fundamentals</li>\r\n<li>Establishing Internet and WAN connectivity (IPv4 and IPv6)</li>\r\n<li>Managing network device security</li>\r\n<li>Operating a medium-sized LAN with multiple switches, supporting VLANs, trunking, and spanning tree</li>\r\n<li>Troubleshooting IP connectivity (IPv4 and IPv6)</li>\r\n<li>Configuring and troubleshooting EIGRP and OSPF (IPv4 and IPv6)</li>\r\n<li>Configuring devices for SNMP, Syslog, and NetFlow access</li>\r\n<li>Managing Cisco device configurations, Cisco IOS images, and licenses</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Implementing Cisco IP Switched Networks (300-115)</h3>\r\n            <div class=\"col-md-12\">\r\n            <ul class=\"listTick\">\r\n			  <li>Drawing Lines</li>\r\n<li>Introduction to Campus Networks</li>\r\n<li>Defining Virtual Networks (VLANs)</li>\r\n<li>Implementing Spanning Tree</li>\r\n<li>Implementing InterVLAN Routing</li>\r\n<li>Implement High Availability in a Campus Environment</li>\r\n<li>Wireless Client Access</li>\r\n<li>Minimizing Service Loss and Data Theft in a Campus Network</li>\r\n<li>Configuring Campus Switches to Support Voice</li>\r\n</ul>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Troubleshooting and Maintaining Cisco IP Networks (300-135)</h3>\r\n            <div class=\"col-md-12\">\r\n            <ul class=\"listTick\">\r\n<li>Layer 2 switching</li>\r\n<li>VLANs, VLAN access control lists, port security</li>\r\n<li>Switch security issues</li>\r\n<li>Link aggregation protocols</li>\r\n<li>Spanning Tree Protocol (STP)</li>\r\n<li>Inter-VLAN routing solutions</li>\r\n<li>First Hop Redundancy Protocols (FHRPs) - HSRP, VRRP, and GLBP</li>\r\n<li>Branch office operations</li>\r\n<li>Enhanced Interior Gateway Routing Protocol (EIGRP)</li>\r\n<li>Open Shortest Path First (OSPF)</li>\r\n<li>Layer 3 path control</li>\r\n               </ul>\r\n            </div>', 'CCNA/CCNP Course'),
(11, 'MongoDB', 'Introduction and Overview\r\nCreating, Reading and Updating Data (CRUD)\r\nPerformance\r\nReplication\r\nReplication, Part 2\r\nScalability\r\nBackup and Recovery', 'MogoDB '),
(12, 'Internet Of Things', '<h3 class=\"text-blue\">Business Overview of Why IoT is so important</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n                <li>Case Studies from Nest, CISCO and top industries</li>\r\n                <li>IoT adaptation rate in North American &amp; and how they are aligning their future business model and operation around IoT</li>\r\n                <li>Broad Scale Application Area</li>\r\n                <li>Smart house and smart city</li>\r\n                <li>Industrial Internet</li>\r\n                <li>Smart Cars</li>\r\n                <li>Wearables</li>\r\n                <li>Home healthcare</li>\r\n                <li>Business Rule generation for IoT</li>\r\n                <li>3 layered architecture of Big Data –Physical (Sensors), Communication and Data Intelligence</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Introduction of IoT: All about Sensors</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n                 <li>Basic function and architecture of a sensor –Sensor body, sensor mechanism, sensor calibration, sensor maintenance, cost and pricing structure, legacy and modern sensor network- All basics about the sensors</li>\r\n                 <li>Development of sensor electronics- IoT vs legacy and open source vs traditional PCB design style</li>\r\n                 <li>Development of Sensor communication protocols –history to modern days. Legacy protocols like Modbus, relay, HART to modern day Zigbee, Zwave, X10,Bluetooth, ANT etc..</li>\r\n                 <li>Business driver for sensor deployment- FDA/EPA regulation, Fraud/tempering detection, supervision, Quality control and process management</li>\r\n                 <li>Different Kind of Calibration Techniques-manual, automation, infield, primary and secondary calibration –their implication in IoT</li>\r\n                 <li>Powering options for sensors-Battery, solar, Witricity. Mobile and PoE</li>\r\n\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Introduction to Sensor Network and Wireless protocol</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n                  <li>What is a sensor network? What is Ad-hoc network ?</li>\r\n                  <li>Wireless vs. Wireline network</li>\r\n                  <li>WiFi- 802.11 families: N to S- application of each standards and common vendors.</li>\r\n                  <li>Zigbee and Zwave-advantage of low power mesh networking. Long distance Zigbee. Introduction to different Zigbee chips:</li>\r\n                  <li>Bluetooth/BLE: Low power vs high power, speed of detection, class ofBLE. Introduction of Bluetooth vendors &amp; their review :</li>\r\n                  <li>Creating network with Wireless protocols such as Piconet by BLE</li>\r\n                  <li>Protocol stacks and packet structure for BLE and Zigbee</li>\r\n                  <li>Other long distance RF communication link</li>\r\n                  <li>LOS vs NLOS links</li>\r\n                  <li>Sensor network packet architecture</li>\r\n                  <li>Capacity and throughput calculation</li>\r\n                  <li>Application issues in wireless protocols- power consumption, reliability, PER, QoS, LOS</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Review of Electronics Platform, production and cost projection</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n                 <li>PCB vs FPGA vs ASIC design-how to take decision</li>\r\n                 <li>Prototyping electronics vs Production electronics</li>\r\n                 <li>QA certificate for IoT- CE/CSA/UL/IEC/RoHS/IP65: What are those and</li>\r\n				 <li>Basic introduction of multi-layer PCB design and its workflow when needed?</li>\r\n				 <li>Electronics reliability-basic concept of FIT and early mortality rate</li>\r\n				 <li>Environmental and reliability testing-basic concepts</li>\r\n				 <li>Basic Open source platforms: Adruino, Rasberry Pi, Beaglebone, RedBack, Diamond Back</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Conceiving a new IoT product- Product requirement document for IoT</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n                 <li>State of the present art and review of existing technology in the marketplace</li>\r\n          <li>Suggestion for new features and technologies based on market analysis and patent issues</li>\r\n          <li>Detailed technical specs for new products- System, software, hardware, mechanical, installation etc.</li>\r\n          <li>Packaging and documentation requirements</li>\r\n          <li>Servicing and customer support requirements</li>\r\n          <li>High level design (HLD) for understanding of product concept</li>\r\n          <li>Release plan for phase wise introduction of the new features</li>\r\n          <li>Skill set for the development team and proposed project plan -cost &amp; duration</li>\r\n          <li>Target manufacturing price</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Introduction to Mobile app platform &amp; Middleware for IoT</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n				<li>Protocol stack of Mobile app for IoT</li>\r\n          <li>Fundamentals of WAP ( Wireless application protocols)</li>\r\n          <li>Mobile to server integration –what are the factors to look out</li>\r\n          <li>What are the intelligent layer that can be introduced at Mobile app level ?</li>\r\n          <li>iBeacon in IoS</li>\r\n          <li>Global vs Local ID-GUID concept for secured IoT network</li>\r\n          <li>IoT-Middleware case study-1 Axeda</li>\r\n          <li>IoT Middleware case study-2 Xively</li>\r\n\r\n              </ul>\r\n            </div>\r\n			 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Machine learning for intelligent IoT</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>Introduction to Machine learning</li>\r\n          <li>Learning classification techniques</li>\r\n          <li>Bayesian Prediction-preparing training file</li>\r\n          <li>Support Vector Machine</li>\r\n          <li>Image and video analytic for IoT</li>\r\n          <li>Fraud and alert analytic through IoT</li>\r\n          <li>Bio –metric ID integration with IoT</li>\r\n          <li>Real Time Analytic/Stream Analytic</li>\r\n          <li>Scalability issues of IoT and machine learning</li>\r\n          <li>What are the architectural implementation of Machine learning for IoT</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Analytic Engine for IoT</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>Insight analytic</li>\r\n          <li>Visualization analytic</li>\r\n          <li>Structured predictive analytic</li>\r\n          <li>Unstructured predictive analytic</li>\r\n          <li>Recommendation Engine</li>\r\n          <li>Pattern detection</li>\r\n          <li>Rule/Scenario discovery –failure, fraud, optimization</li>\r\n          <li>Root cause discovery</li>\r\n              </ul>\r\n            </div>\r\n						 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Iaas/Paas/Saas-IoT data, platform and software as a service revenue model</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n           <li>Iaas : Information as a service-evolving models</li>\r\n          <li>Mechanism of security breach in IOT layer for Iaas</li>\r\n          <li>Middleware for Iaas business implementation in healthcare, homeautomation and farming</li>\r\n          <li>Iaas case study for vehicular information for Auto-insurance and Agriculture</li>\r\n          <li>Paas: Platform as a service in IoT. Case studies of some of the IoT middleware</li>\r\n          <li>Saas : Software/System as service for IoT business models</li>\r\n          <li>European legislation for security in IoT platform</li>\r\n          <li>Firewalling and IPS</li>\r\n          <li>Updates and patches</li>\r\n              </ul>\r\n            </div>\r\n								 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Database &amp; Platform implementation for IoT : Cloud based IoT platforms</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n            <li>SQL vs NoSQL-Which one is good for your IoT application</li>\r\n          <li>Open sourced vs. Licensed Database</li>\r\n          <li>Available M2M cloud platform</li>\r\n          <li>Basic functionality of IoT cloud platform</li>\r\n          <li>Real Time Analytic</li>\r\n          <li>Batch Analytic</li>\r\n          <li>Data storage</li>\r\n          <li>Data filtering</li>\r\n          <li>Rule engine</li>\r\n          <li>Process mapping</li>\r\n          <li>Caching of Data for IoT rule implementation</li>\r\n          <li>Lossless data compression /Data encoding : Huffman and Progressive filtering</li>\r\n          <li>Xively/ Omeg/Ayla/Libellium/CISCO M2M platform /AT &amp;T M2M platform/ Google M2M platform</li>\r\n              </ul>\r\n            </div>\r\n			 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">A few common IoT systems\r\n</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			<li>Home automation</li>\r\n          <li>Energy optimization in Home</li>\r\n          <li>Automotive-OBD /Iaas/Paas for Insurance and Car parking</li>\r\n          <li>IoT-Lock</li>\r\n          <li>Smart Smoke alarm</li>\r\n          <li>BAC ( Blood alcohol monitoring ) for drug abusers under probation</li>\r\n          <li>Pet cam for Pet lovers</li>\r\n          <li>Wearable IOT</li>\r\n          <li>Mobile parking ticketing system</li>\r\n          <li>Indoor location tracking in Retail store</li>\r\n          <li>Home health care</li>\r\n          <li>Smart Sports Watch</li>\r\n\r\n              </ul>\r\n            </div>\r\n						 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Big Data for IoT</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n           <li>4V- Volume, velocity, variety and veracity of Big Data</li>\r\n          <li>Why Big Data is important in IoT</li>\r\n          <li>Big Data vs legacy data in IoT</li>\r\n          <li>Fundamental of Map reduced system (MR)</li>\r\n          <li>Hadoop and HDFS</li>\r\n          <li>Kafka for data messaging and brokerage</li>\r\n          <li>Storm/Bolt/Spout-fundamentals of real time analytic system</li>\r\n          <li>Storage technique for image, Geospatial and video data</li>\r\n          <li>Distributed database like Cassandra and HDFS</li>\r\n          <li>Parallel computing basics for IoT</li>\r\n              </ul>\r\n            </div>', 'Internet Of Things'),
(13, 'Cloud Computing', '<h3 class=\"text-blue\">Introduction to Cloud Computing</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			  <li>Defining cloud computing</li>\r\n<li>Components of a computing cloud</li>\r\n<li>Differentiating types of clouds: public, private, hybrid</li>\r\n<li>Delivering services from the cloud</li>\r\n<li>Categorizing service types</li>\r\n<li>Comparing vendor cloud products: Amazon, Google, Microsoft and others</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Adopting the Cloud</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Key drivers of cloud computing solutions</li>\r\n<li>Instantaneous provisioning of computing resources</li>\r\n<li>Handling varied loads with elasticity and seamless scalability</li>\r\n<li>Tapping into an infinite storage capacity</li>\r\n<li>Cost-effective pay-as-you-use billing models</li>\r\n<li>Evaluating barriers to cloud computing</li>\r\n<li>Handling sensitive data</li>\r\n<li>Aspects of cloud security</li>\r\n<li>Assessing governance solutions</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Exploiting Software as a Service ( SaaS )</h3>\r\n            <div class=\"col-md-12\">\r\n            <ul class=\"listTick\">\r\n			 <li>Characterizing SaaS</li>\r\n<li>Minimizing the need for local hardware and software</li>\r\n<li>Streamlining administration with centralized installation and updates</li>\r\n<li>Optimizing cost and performance with the ability to scale on demand</li>\r\n<li>Comparing service scenarios</li>\r\n<li>Improving collaboration with business productivity tools</li>\r\n<li>Simplifying business process creation by integrating existing components</li>\r\n<li>Inspecting SaaS technologies</li>\r\n<li>Deploying Web applications</li>\r\n<li>Implementing Web services: SOAP, REST</li>\r\n<li>Choosing a development platform</li>\r\n</ul>\r\n              </ul>\r\n            </div>\r\n<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Delivering Platform as a Service (PaaS)</h3>\r\n            <div class=\"col-md-12\">\r\n            <ul class=\"listTick\">\r\n            \r\n          \r\n<li>Exploring the technical foundation for PaaS</li>\r\n<li>Specifying the components of PaaS</li>\r\n<li>Analyzing vendor PaaS provisions</li>\r\n<li>Selecting an appropriate implementation</li>\r\n<li>Building services with solution stacks</li>\r\n<li>Evaluating the architecture of vendor specific platforms</li>\r\n<li>Becoming familiar with service platform tools</li>\r\n<li>Leveraging the power of scalable middleware</li>\r\n<li>Managing cloud storage</li>\r\n<li>Controlling unstructured data in the cloud</li>\r\n<li>Deploying relational databases in the cloud</li>\r\n<li>Improving data availability</li>\r\n<li>Employing support services</li>\r\n<li>Testing in the cloud</li>\r\n<li>Monitoring cloud-based services</li>\r\n<li>Analyzing portability across platforms</li>\r\n</ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Deploying Infrastructure as a Service ( IaaS )</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n    <li>Enabling technologies</li>\r\n<li>Scalable server clusters</li>\r\n<li>Achieving transparency with platform virtualization</li>\r\n<li>Elastic storage devices</li>\r\n<li>Accessing IaaS</li>\r\n<li>Provisioning servers on demand</li>\r\n<li>Handling dynamic and static IP addresses</li>\r\n<li>Tools and support for management and monitoring</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Building a Business Case</h3>\r\n            <div class=\"col-md-12\">\r\n		<ul class=\"listTick\">\r\n           <li>Calculating the financial implications</li>\r\n<li>Analyzing current and future computing requirements</li>\r\n<li>Comparing in-house facilities to the cloud</li>\r\n<li>Estimating economic factors downstream</li>\r\n<li>Preserving business continuity</li>\r\n<li>Selecting appropriate service-level agreements</li>\r\n<li>Safeguarding access to assets in the cloud</li>\r\n<li>Security, availability and disaster recovery strategies</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Migrating to the Cloud</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			<li>Technical considerations</li>\r\n<li>Rearchitecting applications for the cloud</li>\r\n<li>Integrating the cloud with existing applications</li>\r\n<li>Avoiding vendor lock-in</li>\r\n<li>Planning the migration</li>\r\n<li>Incremental vs</li>\r\n<li>one-step solution</li>\r\n<li>Selecting a vendor</li>\r\n<li>Establishing staff skill requirements</li>\r\n             </ul>\r\n            </div>', 'Cloud Computing'),
(14, 'Android', '<h3 class=\"text-blue\">Introduction to Android</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>The Android family</li>\r\n<li<li>Overview of Android and Android SDK</li>\r\n<li>History of Android</li>\r\n<li>Android features</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Android Architecture overview</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Introduction to OS layers</li>\r\n<li>Linux kernel</li>\r\n<li>Libraries</li>\r\n<li>Android Runtime</li>\r\n<li>Application framework</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Setup of Android Development environment</h3>\r\n            <div class=\"col-md-6\">\r\n              <ul class=\"listTick\">\r\n              <li>System requirements</li>\r\n<li>Eclipse and SDK installation, AVD creation</li>\r\n<li>Creating first Android application</li>\r\n<li>Project Structure</li>\r\n</ul>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Android Application Fundamentals</h3>\r\n            <div class=\"col-md-6\">\r\n              <ul class=\"listTick\">\r\n    <li>Android application building blocks</li>\r\n<li>Activating components</li>\r\n<li>Shutting down components</li>\r\n<li>Life Cycle of Application</li>\r\n<li>Development tools, Manifest File</li>\r\n<li>Life Cycle of Activity</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">User Interface</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n        <li>View Hierarchy and Layouts</li>\r\n<li>UI Events</li>\r\n<li>Building Menus</li>\r\n<li>Notifying users</li>\r\n<li>Creating dialogs</li>\r\n<li>Graphics &amp; Animations</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Main Building Blocks</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n           <li>Activity</li>\r\n<li>Services</li>\r\n<li>Content Providers</li>\r\n<li>Broadcast Receivers</li>\r\n              </ul>\r\n            </div>\r\n			 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Resources</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n          <li>Overview of Android Resources</li>\r\n<li>Creating Resources</li>\r\n<li>Using Resources</li>\r\n<li>Drawable Resources</li>\r\n<li>Animation Resources</li>\r\n              </ul>\r\n            </div>\r\n						 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Data Storage</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>Shared Preferences</li>\r\n<li>Internal Storage (Files)</li>\r\n<li>External Storage(SD Card)</li>\r\n<li>SQLite Databases</li>\r\n              </ul>\r\n            </div>\r\n						 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Android Media API</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Playing audio/video</li>\r\n<li>Media recording</li>\r\n<li>Blue tooth</li>\r\n<li>WiFi</li>\r\n<li>Camera</li>\r\n<li>TelephonyManager</li>\r\n<li>Location Services</li>\r\n<li>Google Maps</li>\r\n<li>Deploying Android Application on Device</li>\r\n               </ul>\r\n            </div>', 'Android App Development'),
(15, 'IOS', '<ul class=\"listTick\">\r\n<li>Define key programming terms relevant to Swift and iOS programming.</li>\r\n    <li>Describe the process of creating iOS apps.</li>\r\n    <li>State the purpose of the Apple developer tools, such as Xcode, Instruments, debugger, analyzer, and iOS Simulator.</li>\r\n    <li>Distinguish well-written code from poorly-written code.</li>\r\n    <li>Recognize patterns and idioms present in the Cocoa Touch API and other Apple frameworks.</li>\r\n    <li>Employ the Apple developer tools to create an iOS app.</li>\r\n    <li>Demonstrate programming best practices in Swift.</li>\r\n    <li>Examine and subdivide app functionality into properly designed components.</li>\r\n    <li>Explain and summarize iOS API features including location, mapping, sensors, gestures, multimedia and user interface components.</li>\r\n    <li>Plan, prepare and build an original iOS app, from concept to working program.</li>\r\n              </ul>', 'IOS with Swift'),
(16, 'Amazon Web Services', '<h2 class=\"mainHeading\">Course Content and Syllabus for Amazon Web Services Training in Noida</h2>\r\n			\r\n            <h3 class=\"text-blue\">Getting started with Amazon cloud</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n            <li>Creating accounts and analyzing the cost breakdown</li>\r\n<li>Evaluating Service Level Agreements (SLA)</li>\r\n<li>Console, command line tools and API</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Overview of the architecture:</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>EC2</li>\r\n<li>S3</li>\r\n<li>EBS</li>\r\n<li>Beanstalk</li>\r\n<li>RDS</li>\r\n<li>VPC</li>\r\n<li>CloudFront</li>\r\n<li>SimpleDB</li>\r\n<li>SQS</li>\r\n<li>CloudWatch</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\"><strong>Achieving Agility with EC2:</strong></h3>\r\n            <div class=\"col-md-6\">\r\n              <ul class=\"listTick\">\r\n</ul>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Managing the EC2 infrastructure:</h3>\r\n            <div class=\"col-md-6\">\r\n              <ul class=\"listTick\">\r\n    <li>Browsing Amazon Machine Images (AMI)</li>\r\n<li>Specifying security groups and key pairs</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Provisioning resources:</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n        <li>Evaluating Elastic Block Store (EBS) and instance store root devices</li>\r\n<li>Assigning elastic IP addresses</li>\r\n<li>Mapping instance types to computing needs</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\"><strong>Implementing Durable and Reliable Storage:</strong></h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Storing data in the cloud:</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n           <li>Persisting off-instance storage with EBS volumes</li>\r\n<li>Creating backups with snapshots</li>\r\n<li>Achieving high durability with SimpleStorage Service</li>\r\n<li>Transmitting data in/out of the Amazon cloud</li>\r\n              </ul>\r\n            </div>\r\n			 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Simplifying the database infrastructure:</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n   <li>Achieving high availability of nonrelational data with SimpleDB</li>\r\n<li>Effortlessly implement a relational database with Relational Database Service (RDS)</li>\r\n              </ul>\r\n            </div>\r\n						 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Creating cost-effective distributed solutions:</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>Decoupling applications with Simple Queue Service</li>\r\n<li>Leveraging CloudFront for high-performance edge cache content delivery</li>\r\n<li>Delivering static and streaming content</li>\r\n              </ul>\r\n            </div>\r\n						 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\"><strong>Adapting EC2 to Your Business Needs:</strong></h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Customizing virtual machines:</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			  <li>Modifying existing images</li>\r\n<li>Creating new images off of running instances</li>\r\n<li>Converting an instance store AMI to an EBS A</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Creating an AWS cloud architecture:</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			  <li>Applying best practices for a cloud solution</li>\r\n<li>Selecting a cloud setup for different use case scenarios</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\"><strong>Handling Dynamic Resource Requirements:</strong></h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			  </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Monitoring from inside or outside of the cloud:</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			  <li>Visualizing utilization metrics with CloudWatch</li>\r\n<li>Setting alarms to send and receive notifications</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Transparently scaling to meet load variations:</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			 <li>Distributing incoming traffic with elastic load balancing</li>\r\n<li>Dynamically adding and removing instances with Auto Scaling</li>\r\n<li>Setting capacity thresholds</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\"><strong>Hosting Applications with Elastic Beanstalk:</strong></h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			  </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Managing application environments:</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			  <li>Customizing and configuring platform stacks</li>\r\n<li>Provisioning application resources with CloudFormation</li>\r\n			  </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\"><strong>AWS Security Features:</strong></h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			  </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Controlling account security:</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			  <li>Configuring access credentials</li>\r\n<li>Managing users with Identity Access Management (IAM)</li>\r\n			  </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Leveraging the Virtual Private Cloud (VPC):</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n			  <li>Provisioning isolated AWS resources</li>\r\n<li>Bridging EC2 instances to your internal network with a VPN</li>\r\n<li>Launching EC2 instances on dedicated hardware</li>\r\n			 </ul>\r\n            </div>', 'AWS ');
INSERT INTO `courses` (`id`, `course`, `content`, `desc`) VALUES
(17, 'Big Data/Hadoop', '<h3 class=\"text-blue\">Hadoop Course Content</h3>\r\n            <div class=\"col-md-12\">\r\n			<ul class=\"listTick\">\r\n			<li>Hadoop Overview, Architecture Considerations, Infrastructure, Platforms and Automation</li>\r\n			</ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Use case walkthrough</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>ETL</li>\r\n<li>Log Analytics</li>\r\n<li>Real Time Analytics</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Hbase for Developers : </h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n           <h3 class=\"text-blue\">NoSQL Introduction</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Traditional RDBMS approach</li>\r\n<li>NoSQL introduction</li>\r\n<li>Hadoop & Hbase positioning</li>\r\n</ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			 <h3 class=\"text-blue\">Hbase Introduction</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>What it is, what it is not, its history and common use-cases</li>\r\n<li>Hbase Client – Shell, exercise</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			 <h3 class=\"text-blue\">Hbase Architecture</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Building Components</li>\r\n<li>Storage, B+ tree, Log Structured Merge Trees</li>\r\n<li>Region Lifecycle</li>\r\n<li>Read/Write Path</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			 <h3 class=\"text-blue\">Hbase Schema Design</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Introduction to hbase schema</li>\r\n<li>Column Family, Rows, Cells, Cell timestamp</li>\r\n<li>Deletes</li>\r\n<li>Exercise - build a schema, load data, query data</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n						 <h3 class=\"text-blue\">Hbase Java API – Exercises</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Connection</li>\r\n<li>CRUD API</li>\r\n<li>Scan API</li>\r\n<li>Filters</li>\r\n<li>Counters</li>\r\n<li>Hbase MapReduce</li>\r\n<li>Hbase Bulk load</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n						 <h3 class=\"text-blue\">Hbase Operations, cluster management</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Performance Tuning</li>\r\n<li>Advanced Features</li>\r\n<li>Exercise</li>\r\n<li>Recap and Q&A</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">MapReduce for Developers</h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Introduction</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n			<li>Traditional Systems / Why Big Data / Why Hadoop</li>\r\n<li>Hadoop Basic Concepts/Fundamentals</li>\r\n</ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Hadoop in the Enterprise</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n			<li>Where Hadoop Fits in the Enterprise</li>\r\n<li>Review Use Cases</li>\r\n</ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Architecture</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Hadoop Architecture & Building Blocks</li>\r\n<li>HDFS and MapReduce</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Hadoop CLI</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Walkthrough</li>\r\n<li>Exercise</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">MapReduce Programming</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Fundamentals</li>\r\n<li>Anatomy of MapReduce Job Run</li>\r\n<li>Job Monitoring, Scheduling</li>\r\n<li>Sample Code Walk Through</li>\r\n<li>Hadoop API Walk Through</li>\r\n<li>Exercise</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">MapReduce Formats</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Input Formats, Exercise</li>\r\n<li>Output Formats, Exercise</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Hadoop File Formats</h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">MapReduce Design Considerations</h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Hadoop File Formats </h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">MapReduce Algorithms</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Walkthrough of 2-3 Algorithms</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">MapReduce Features</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Counters, Exercise</>\r\n<li>Map Side Join, Exercise</li>\r\n<li>Reduce Side Join, Exercise</li>\r\n<li>Sorting, Exercise</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Use Case A (Long Exercise)</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Input Formats, Exercise\r\n<li>Output Formats, Exercise</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">MapReduce Testing</h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Hadoop Ecosystem</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Oozie</li>\r\n<li>Flume</li>\r\n<li>Sqoop</li>\r\n<li>Exercise 1 (Sqoop)</li>\r\n<li>Streaming API</li>\r\n<li>Exercise 2 (Streaming API)</li>\r\n<li>Hcatalog</li>\r\n<li>Zookeeper</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">HBase Introduction</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Introduction</li>\r\n<li>HBase Architecture</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">VIEW Types</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Default Views</li>\r\n<li>Overriden Views</li>\r\n<li>Normal Views</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">MapReduce Performance Tuning</h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Development Best Practice and Debugging</h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Apache Hadoop for Administrators</h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Hadoop Fundamentals and Architecture</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Why Hadoop, Hadoop Basics and Hadoop Architecture</li>\r\n<li>HDFS and Map Reduce</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Hadoop Ecosystems Overview</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Hive</li>\r\n<li>Hbase</li>\r\n<li>ZooKeeper</li>\r\n<li>Pig</li>\r\n<li>Mahout</li>\r\n<li>Flume</li>\r\n<li>Sqoop</li>\r\n<li>Oozie</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Hardware and Software requirements</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Hardware, Operating System and Other Software</li>\r\n<li>Management Console</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Deploy Hadoop ecosystem services</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Hive</li>\r\n<li>ZooKeeper</li>\r\n<li>HBase</li>\r\n<li>Administration</li>\r\n<li>Pig</li>\r\n<li>Mahout</li>\r\n<li>Mysql</li>\r\n<li>Setup Security</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Enable Security – Configure Users, Groups, Secure HDFS, MapReduce, HBase and Hive</h3>\r\n            <div class=\"col-md-6\">\r\n            <ul class=\"listTick\">\r\n<li>Configuring User and Groups</li>\r\n<li>Configuring Secure HDFS</li>\r\n<li>Configuring Secure MapReduce</li>\r\n<li>Configuring Secure HBase and Hive</li>\r\n              </ul>\r\n            </div>\r\n<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Manage and Monitor your cluster<h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Command Line Interface</h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n				<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Troubleshooting your cluster</h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n				<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Introduction to Big Data and Hadoop</h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Hadoop Overview</h3>\r\n            <div class=\"col-md-6\">\r\n			 <ul class=\"listTick\">\r\n<li>Why Hadoop</li>\r\n<li>Hadoop Basic Concepts</li>\r\n<li>Hadoop Ecosystem &ndash; MapReduce, Hadoop Streaming, Hive, Pig, Flume, Sqoop, Hbase, Oozie, Mahout</li>\r\n<li>Where Hadoop fits in the Enterprise</li>\r\n<li>Review use cases</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Apache Hive & Pig for Developers</h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Overview of Hadoop</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Why Hadoop</li>\r\n<li>Hadoop Basic Concepts</li>\r\n<li>Hadoop Ecosystem &ndash; MapReduce, Hadoop Streaming, Hive, Pig, Flume, Sqoop, Hbase, Oozie, Mahout</li>\r\n<li>Where Hadoop fits in the Enterprise</li>\r\n<li>Review use cases</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Overview of Hadoop</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Big Data and the Distributed File System</li>\r\n<li>MapReduce</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Hive Introduction</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Why Hive?</li>\r\n<li>Compare vs SQL</li>\r\n<li>Use Cases</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Hive Architecture – Building Blocks</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Hive CLI and Language (Exercise)</li>\r\n<li>HDFS Shell</li>\r\n<li>Hive CLI</li>\r\n<li>Data Types</li>\r\n<li>Hive Cheat-Sheet</li>\r\n<li>Data Definition Statements</li>\r\n<li>Data Manipulation Statements</li>\r\n<li>Select, Views, GroupBy, SortBy/DistributeBy/ClusterBy/OrderBy, Joins</li>\r\n<li>Built-in Functions</li>\r\n<li>Union, Sub Queries, Sampling, Explain</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Hive Architecture – Building Blocks</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Hive CLI and Language (Exercise)</li>\r\n<li>HDFS Shell</li>\r\n<li>Hive CLI</li>\r\n<li>Data Types</li>\r\n<li>Hive Cheat-Sheet</li>\r\n<li>Data Definition Statements</li>\r\n<li>Data Manipulation Statements</li>\r\n<li>Select, Views, GroupBy, SortBy/DistributeBy/ClusterBy/OrderBy, Joins</li>\r\n<li>Built-in Functions</li>\r\n<li>Union, Sub Queries, Sampling, Explain</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Hive Architecture – Building Blocks</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Hive CLI and Language (Exercise)</li>\r\n<li>HDFS Shell</li>\r\n<li>Hive CLI</li>\r\n<li>Data Types</li>\r\n<li>Hive Cheat-Sheet</li>\r\n<li>Data Definition Statements</li>\r\n<li>Data Manipulation Statements</li>\r\n<li>Select, Views, GroupBy, SortBy/DistributeBy/ClusterBy/OrderBy, Joins</li>\r\n<li>Built-in Functions</li>\r\n<li>Union, Sub Queries, Sampling, Explain</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Hive Usecase implementation -(Exercise)</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Use Case 1</li>\r\n<li>Use Case 2</li>\r\n<li>Best Practices</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Advance Features</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Transform and Map-Reduce Scripts</li>\r\n<li>Custom UDF</li>\r\n<li>UDTF</li>\r\n<li>SerDe</li>\r\n<li>Recap and Q&amp;A</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Pig Introduction</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Position Pig in Hadoop ecosystem</li>\r\n<li>Why Pig and not MapReduce</li>\r\n<li>Simple example (slides) comparing Pig and MapReduce</li>\r\n<li>Who is using Pig now and what are the main use cases</li>\r\n<li>Pig Architecture</li>\r\n<li>Discuss high level components of Pig</li>\r\n<li>Pig Grunt - How to Start and Use</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Pig Latin Programming</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Data Types</li>\r\n<li>Cheat sheet</li>\r\n<li>Schema</li>\r\n<li>Expressions</li>\r\n<li>Commands and Exercise</li>\r\n<li>Load, Store, Dump, Relational Operations,Foreach, Filter, Group, Order By, Distinct, Join, Cogroup,Union, Cross, Limit, Sample, Parallel</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Use Cases (working exercise)</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Use Case 1</li>\r\n<li>Use Case 2</li>\r\n<li>Use Case 3 (compare pig and hive)</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Advanced Features, UDFs</h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Best Practices and common pitfalls</h3>\r\n            <div class=\"col-md-6\">\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Mahout & Machine Learning</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Mahout Overview</li>\r\n<li>Mahout Installation</li>\r\n<li>Introduction to the Math Library</li>\r\n<li>Vector implementation and Operations (Hands-on exercise)</li>\r\n<li>Matrix Implementation and Operations (Hands-on exercise)</li>\r\n<li>Anatomy of a Machine Learning Application</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Classification</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Introduction to Classification</li>\r\n<li>Classification Workflow</li>\r\n<li>Feature Extraction</li>\r\n<li>Classification Techniques (Hands-on exercise)</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Evaluation (Hands-on exercise)</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Clustering</li>\r\n<li>Use Cases</li>\r\n<li>Clustering algorithms in Mahout</li>\r\n<li>K-means clustering (Hands-on exercise)</li>\r\n<li>Canopy clustering (Hands-on exercise)</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Clustering</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Mixture Models</li>\r\n<li>Probabilistic Clustering &ndash; Dirichlet (Hands-on exercise)</li>\r\n<li>Latent Dirichlet Model (Hands-on exercise)</li>\r\n<li>Evaluating and Improving Clustering quality (Hands-on exercise)</li>\r\n<li>Distance Measures (Hands-on exercise)</li>\r\n              </ul>\r\n            </div>\r\n			<div class=\"clearfix\"></div>\r\n			<h3 class=\"text-blue\">Recommendation Systems</h3>\r\n            <div class=\"col-md-6\">\r\n			<ul class=\"listTick\">\r\n<li>Overview of Recommendation Systems</li>\r\n<li>Use cases</li>\r\n<li>Types of Recommendation Systems</li>\r\n<li>Collaborative Filtering (Hands-on exercise)</li>\r\n<li>Recommendation System Evaluation (Hands-on exercise)</li>\r\n<li>Similarity Measures</li>\r\n<li>Architecture of Recommendation Systems</li>\r\n<li>Wrap Up</li>\r\n               </ul>\r\n            </div>', 'Big Data'),
(18, 'Web Designing', '<h3 class=\"text-blue\">What is Web Design</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>Introduction to Web Design</li>\r\n<li>Domain Names &amp; DNS</li>\r\n<li>Client and Server Software.</li>\r\n<li>Static&amp; Dynamic</li>\r\n<li>Careers in Web Technologies</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Adobe Photoshop CSS-2</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Introduction of Stock Photography</li>\r\n<li>Types of Images and Image Editing Tools</li>\r\n<li>Introduction to Adobe Photoshop</li>\r\n<li>Using Photoshop Tools</li>\r\n<li>Layers, Actions and Filters</li>\r\n<li>Creating Custom Effects</li>\r\n<li>Design Banners, Basic Website Layout</li>\r\n<li>Design Complex website Layout</li>\r\n<li>Conversation of PSD to XHTML</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Adobe Flash</h3>\r\n            <div class=\"col-md-6\">\r\n              <ul class=\"listTick\">\r\n              <li>Introduction to Animation</li>\r\n<li>Introduction to Adobe Flash</li>\r\n<li>Tools in Adobe Flash</li>\r\n<li>Frame Animation</li>\r\n<li>Various Flash Effects</li>\r\n<li>Creating Flash Banners</li>\r\n<li>Creating Flash Intro&rsquo;s</li>\r\n<li>Creating Flash Website</li>\r\n<li>Basics of Action Scripting.</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">HTML &amp; DHTML</h3>\r\n            <div class=\"col-md-6\">\r\n              <ul class=\"listTick\">\r\n    <li>Structure of HTML</li>\r\n<li>Basic HTML Tags</li>\r\n<li>Advanced HTML Tags</li>\r\n<li>Difference between HTML &amp; DHTML</li>\r\n<li>DHTML Basic tags</li>\r\n<li>Introduction to Doc Types</li>\r\n<li>Creating Simple HTML Pages</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Cascading Style Sheets</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n             <li>Introduction to CSS</li>\r\n<li>Types of style sheets</li>\r\n<li>Types of CSS Selectors</li>\r\n<li>Complete CSS properties</li>\r\n<li>Converting Table layout to CSS</li>\r\n<li>Custom CSS Layout Design</li>\r\n<li>Creating simple and dropdown menus</li>\r\n<li>Creating Appealing forms using CSS</li>\r\n<li>CSS Tips and Tricks with Hacks</li>\r\n              </ul>\r\n            </div>\r\n            <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Java Script</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n           <li>Client and Server side scripting</li>\r\n<li>Introduction to Java Scripting</li>\r\n<li>Types of Java Scripts</li>\r\n<li>Variables, operators, loops</li>\r\n<li>Objects, Events and DOM</li>\r\n<li>Common java script functions</li>\r\n<li>Using Java script in Dreamweaver</li>\r\n<li>Java Script Validations</li>\r\n<li>Implementing Menus, Galleries etc</li>\r\n<li>Introduction to Ajax</li>\r\n<li>Real time Ajax Examples</li>\r\n              </ul>\r\n            </div>\r\n			 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">How to Create Website</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n               <li>Client Requirements/Specifications</li>\r\n<li>Creating a concept and layout</li>\r\n<li>Choosing a Color Scheme</li>\r\n<li>Choosing Stock Photography</li>\r\n<li>Texture and Typography</li>\r\n<li>Design a Professional Layout</li>\r\n<li>Conversation of PSD to CSS</li>\r\n<li>Implementing JavaScript.</li>\r\n              </ul>\r\n            </div>\r\n						 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Web Hosting By FTP</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n              <li>Web Hosting Basics</li>\r\n<li>Types of Hosting Packages</li>\r\n<li>Changing Name Servers</li>\r\n<li>Linux and Windows CP</li>\r\n<li>Using FTP Client</li>\r\n<li>Maintaining a Website</li>\r\n              </ul>\r\n            </div>\r\n						 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Adobe Dreamweaver CS3</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Intro to Adobe Dreamweaver</li>\r\n<li>Learning the interface</li>\r\n<li>Defining a Dreamweaver site</li>\r\n<li>Adding Content and Multimedia.</li>\r\n<li>Creating user submission forms.</li>\r\n<li>Importing a website design</li>\r\n<li>Dynamic Features</li>\r\n              </ul>\r\n            </div>\r\n								 <div class=\"clearfix\"></div>\r\n            <h3 class=\"text-blue\">Testing and Implementation</h3>\r\n            <div class=\"col-md-12\">\r\n              <ul class=\"listTick\">\r\n<li>Various Browser Versions</li>\r\n<li>Validating the dHTML &amp; CSS</li>\r\n<li>Common Compatibility Issues</li>\r\n              </ul>\r\n            </div>', 'Web Designing'),
(19, 'HR Journalist', '<h3 class=\"text-blue\">About HR</h3>\r\n<div class=\"col-md-12\">\r\n  <ul class=\"listTick\">\r\n    <li>Organisation Chart and where HR fits in</li>\r\n    <li> HR Department and functions under HR</li>\r\n    <li>Role and Responsibilities of HR</li>\r\n  </ul>\r\n</div>\r\n<h3 class=\"text-blue\">Competency Mapping</h3>\r\n<div class=\"col-md-12\">\r\n  <ul class=\"listTick\">\r\n    <li>Identifying competencies based on Roles</li>\r\n    <li>Designing KRAs and KPIs</li>\r\n    <li>Mapping competencies to PMS</li>\r\n  </ul>\r\n</div>\r\n<h3 class=\"text-blue\">Recruitment and selection</h3>\r\n<div class=\"col-md-12\">\r\n  <ul class=\"listTick\">\r\n    <li>How to create job descriptions based on KRAs</li>\r\n    <li>Preparing effective Manpower Requisition form</li>\r\n    <li>Various platforms to source CVs</li>\r\n    <li>Posting Jobs on Naukri. Com</li>\r\n    <li>Types of Interviews and process of selection </li>\r\n    <li>Preparing Interview Assessment forms based on rating and review methods</li>\r\n    <li>Salary Negotiations and creating CTC</li>\r\n    <li>Offer and Appointment letter creation</li>\r\n  </ul>\r\n</div>\r\n<h3 class=\"text-blue\">On-Boarding</h3>\r\n<div class=\"col-md-12\">\r\n  <ul class=\"listTick\">\r\n    <li>Joining formalities and creating employee file</li>\r\n    <li>Creating Org Induction Plan</li>\r\n    <li>New Hire Orientation </li>\r\n    <li>Develop customised Induction Plan</li>\r\n    <li>Critical check points and tracking progress</li>\r\n  </ul>\r\n</div>\r\n<h3 class=\"text-blue\">Payroll</h3>\r\n<div class=\"col-md-12\">\r\n  <ul class=\"listTick\">\r\n    <li>Designing Salary structure </li>\r\n    <li>Variable and other components </li>\r\n    <li>Payroll compliance and core rules</li>\r\n    <li>EPF and ESIC rules and compliance</li>\r\n    <li>Required forms,  challans and portals</li>\r\n    <li>Income tax- deceleration, calculation and assessment</li>\r\n  </ul>\r\n</div>\r\n<h3 class=\"text-blue\">Leave and Attendance Management</h3>\r\n<div class=\"col-md-12\">\r\n  <ul class=\"listTick\">\r\n    <li>Leave and attendance compliance </li>\r\n    <li>Types of leaves and flexi attendance </li>\r\n    <li>Calculating salary on attendance basis</li>\r\n    <li>Complete Payroll management end-to-end with hands-on practice on excel and web portal</li>\r\n  </ul>\r\n</div>\r\n<h3 class=\"text-blue\">Training and development</h3>\r\n<div class=\"col-md-12\">\r\n  <ul class=\"listTick\">\r\n    <li>Identification of Training needs based on defined KPIs and Competencies</li>\r\n    <li>Training process, scope/role of HR and types</li>\r\n    <li>TNI forms and tools, skills gap analysis</li>\r\n    <li>AIDDIE approach to content development </li>\r\n    <li>Measuring training effectiveness and ROI</li>\r\n    <li>KirckPatrick model to measure training impact</li>\r\n  </ul>\r\n</div>\r\n<h3 class=\"text-blue\">Performance Management System</h3>\r\n<div class=\"col-md-12\">\r\n  <ul class=\"listTick\">\r\n    <li>PMS reviews, types and methodologies </li>\r\n    <li>Understanding Bell Curve approach </li>\r\n    <li>360 degree appraisal and feedback system</li>\r\n    <li>9Box matrix for performance review</li>\r\n    <li>Designing PMS forms and measuring results </li>\r\n    <li>PMS assessment and overall rating results</li>\r\n  </ul>\r\n</div>\r\n<h3 class=\"text-blue\">Organisation Development</h3>\r\n<div class=\"col-md-12\">\r\n  <ul class=\"listTick\">\r\n    <li>OD and HR role </li>\r\n    <li>OD interventions and ideas</li>\r\n    <li>HR role in org development projects</li>\r\n  </ul>\r\n</div>\r\n<h3 class=\"text-blue\">Compliance and Relates Laws</h3>\r\n<h3 class=\"text-blue\">Designing HR SOP,  Policies, Forms and Documents</h3>\r\n<h3 class=\"text-blue\">HR Audits and Critical check points</h3>\r\n<h3 class=\"text-blue\">Creating Performance Improvement Plans (PIP), Performance Development Plan (PDP)</h3>\r\n', 'HR Journalist');
